-- ============================================================
-- DATABASE 3: HUMAN RESOURCES & PAYROLL SYSTEM (PeopleCore)
-- Tables: companies, departments, job_roles, employees,
--         employee_documents, salaries, payroll, leaves,
--         attendance, performance_reviews, training, trainings_attended,
--         benefits, grievances, job_postings, applications
-- ============================================================

CREATE DATABASE IF NOT EXISTS peoplecore_hr;
USE peoplecore_hr;

CREATE TABLE job_roles (
  role_id INT PRIMARY KEY AUTO_INCREMENT,
  role_title VARCHAR(100) NOT NULL,
  role_level ENUM('Entry','Junior','Mid','Senior','Lead','Manager','Director','VP','C-Level'),
  min_salary DECIMAL(12,2),
  max_salary DECIMAL(12,2),
  description TEXT
);


CREATE TABLE departments (
  dept_id INT PRIMARY KEY AUTO_INCREMENT,
  dept_name VARCHAR(100) NOT NULL,
  dept_code VARCHAR(10) UNIQUE,
  location VARCHAR(100),
  budget DECIMAL(15,2),
  head_count INT DEFAULT 0
);


CREATE TABLE employees (
  employee_id INT PRIMARY KEY AUTO_INCREMENT,
  dept_id INT NOT NULL,
  role_id INT NOT NULL,
  manager_id INT,
  first_name VARCHAR(50) NOT NULL,
  last_name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  phone VARCHAR(20),
  gender ENUM('Male','Female','Other'),
  date_of_birth DATE,
  hire_date DATE,
  exit_date DATE,
  employment_type ENUM('Full-time','Part-time','Contract','Intern'),
  employment_status ENUM('Active','Resigned','Terminated','On Leave','Retired'),
  work_location ENUM('Office','Remote','Hybrid'),
  city VARCHAR(100),
  FOREIGN KEY (dept_id) REFERENCES departments(dept_id),
  FOREIGN KEY (role_id) REFERENCES job_roles(role_id)
);


CREATE TABLE salaries (
  salary_id INT PRIMARY KEY AUTO_INCREMENT,
  employee_id INT NOT NULL,
  basic_salary DECIMAL(12,2),
  hra DECIMAL(12,2),
  special_allowance DECIMAL(12,2),
  pf_employee DECIMAL(12,2),
  pf_employer DECIMAL(12,2),
  professional_tax DECIMAL(8,2),
  gross_salary DECIMAL(12,2),
  net_salary DECIMAL(12,2),
  effective_from DATE,
  FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);


CREATE TABLE payroll (
  payroll_id INT PRIMARY KEY AUTO_INCREMENT,
  employee_id INT NOT NULL,
  month INT NOT NULL,
  year INT NOT NULL,
  working_days INT,
  days_worked INT,
  leaves_taken INT,
  overtime_hours DECIMAL(6,2) DEFAULT 0,
  gross_salary DECIMAL(12,2),
  deductions DECIMAL(12,2),
  net_pay DECIMAL(12,2),
  payment_date DATE,
  payment_status ENUM('Processed','Pending','Failed','On Hold'),
  FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);


CREATE TABLE leaves (
  leave_id INT PRIMARY KEY AUTO_INCREMENT,
  employee_id INT NOT NULL,
  leave_type ENUM('Sick','Casual','Annual','Maternity','Paternity','Emergency','Unpaid'),
  from_date DATE,
  to_date DATE,
  days_count INT,
  reason TEXT,
  applied_date DATE,
  approved_by INT,
  status ENUM('Pending','Approved','Rejected','Cancelled'),
  FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);


CREATE TABLE attendance (
  attendance_id INT PRIMARY KEY AUTO_INCREMENT,
  employee_id INT NOT NULL,
  attendance_date DATE,
  check_in_time TIME,
  check_out_time TIME,
  hours_worked DECIMAL(4,2),
  status ENUM('Present','Absent','Half Day','Work From Home','Holiday','Leave'),
  remarks VARCHAR(200),
  FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);


CREATE TABLE performance_reviews (
  review_id INT PRIMARY KEY AUTO_INCREMENT,
  employee_id INT NOT NULL,
  reviewer_id INT NOT NULL,
  review_period VARCHAR(20),
  review_date DATE,
  goals_score DECIMAL(3,1),
  skills_score DECIMAL(3,1),
  behavior_score DECIMAL(3,1),
  overall_score DECIMAL(3,1),
  rating ENUM('Outstanding','Exceeds Expectations','Meets Expectations','Needs Improvement','Unsatisfactory'),
  comments TEXT,
  increment_percent DECIMAL(5,2) DEFAULT 0,
  FOREIGN KEY (employee_id) REFERENCES employees(employee_id)
);


CREATE TABLE job_postings (
  posting_id INT PRIMARY KEY AUTO_INCREMENT,
  dept_id INT NOT NULL,
  role_id INT NOT NULL,
  title VARCHAR(200),
  description TEXT,
  location VARCHAR(100),
  employment_type ENUM('Full-time','Part-time','Contract','Intern'),
  experience_required VARCHAR(50),
  posted_date DATE,
  closing_date DATE,
  status ENUM('Open','Closed','On Hold','Filled'),
  openings INT,
  FOREIGN KEY (dept_id) REFERENCES departments(dept_id),
  FOREIGN KEY (role_id) REFERENCES job_roles(role_id)
);


CREATE TABLE applications (
  application_id INT PRIMARY KEY AUTO_INCREMENT,
  posting_id INT NOT NULL,
  applicant_name VARCHAR(100),
  email VARCHAR(100),
  phone VARCHAR(20),
  years_of_experience INT,
  current_ctc DECIMAL(12,2),
  expected_ctc DECIMAL(12,2),
  notice_period_days INT,
  application_date DATE,
  status ENUM('Applied','Shortlisted','Interview Scheduled','Selected','Rejected','Offer Sent','Joined','Withdrew'),
  source ENUM('LinkedIn','Naukri','Indeed','Referral','Company Website','Walk-in','Agency'),
  FOREIGN KEY (posting_id) REFERENCES job_postings(posting_id)
);

