SELECT * FROM users;
INSERT INTO users (name) VALUES ('Alice');
UPDATE users SET name = 'Bob' WHERE id = 1;
DELETE FROM users WHERE id = 1;

SELECT name, email FROM users
WHERE age > 18
ORDER BY name ASC;

SELECT * FROM products
WHERE (category = 'Electronics' OR category = 'Computers')
AND price < 1000;

SELECT orders.id, customers.name
FROM orders
INNER JOIN customers ON orders.customer_id = customers.id;

CREATE DATABASE my_app;
USE my_app;
DROP DATABASE old_app;

CREATE TABLE users (
    id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

UPDATE products p
JOIN discounts d ON p.category_id = d.category_id
SET p.price = p.price * (1 - d.percentage);

DELIMITER //
CREATE PROCEDURE GetCustomerLevel(IN custId INT, OUT lvl VARCHAR(20))
BEGIN
   DECLARE credit DECIMAL(10,2);
   SELECT creditLimit INTO credit FROM customers WHERE customerNumber = custId;
   IF credit > 50000 THEN
      SET lvl = 'PLATINUM';
   ELSE
      SET lvl = 'SILVER';
   END IF;
END //
DELIMITER ;

CREATE VIEW high_value_orders AS
SELECT order_id, total_amount 
FROM orders WHERE total_amount > 1000;

SELECT CONCAT(first_name, ' ', last_name) AS full_name,
UPPER(city)
FROM customers;

SELECT name, salary,
RANK() OVER (ORDER BY salary DESC) as salary_rank
FROM employees;

